mapper.sql:
from code a 
left join codeGroup b on b.seq = a.codeGroup_seq
 WHERE 1=1

AND a.codeDelNY = #{shCodeDelNY}




	AND a.codeGroup_seq = #{shValue}
	AND b.nameKr LIKE CONCAT('%', #{shValue},'%')
	AND a.codeNameKr LIKE CONCAT('%', #{shValue},'%')
	AND a.codeName LIKE CONCAT('%', #{shValue},'%')



	AND a.codeRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.codeModDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
UPDATE code
SET
	codeGroup_seq = #{codeGroup_seq}
	,codeNameKr = #{codeNameKr}
	,codeName = #{codeName}
	,codeUseNY = #{codeUseNY}
	,codeDelNY = #{codeDelNY}
	,codeModDate = now()
WHERE
	seq = #{seq}
