mapper.sql:
 	FROM codeGroup a
	WHERE 1=1

	AND a.cgDelNY = #{shDelNY}



		AND a.seq = #{shCgValue}
		AND a.nameKr LIKE CONCAT('%', #{shCgValue},'%')
		AND a.name LIKE CONCAT('%', #{shCgValue},'%')



		AND a.regDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.modDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO codeGroup(
	codeGroupCode
	,nameKr
	,name
	,codeNum
	,cgDelNY
	,cgUseNY
	,regDate
)
VALUES (
	#{codeGroupCode}
	,#{nameKr}
	,#{name}
	,#{codeNum}
	,0
	,1
	,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE codeGroup
SET
	codeGroupCode = #{codeGroupCode}
	,nameKr = #{nameKr}
	,name = #{name}
	,codeNum = #{codeNum}
	,cgDelNY = #{cgDelNY}
	,cgUseNY = #{cgUseNY}
	,modDate = now()
WHERE
	seq = #{seq}
