mapper.sql:
FROM member a
left join transport b on b.member_seq = a.seq
WHERE 1=1

AND a.delNY = #{shdelNY}
AND a.gender = #{shGender}
AND a.position = #{shPosition}



	AND a.name LIKE CONCAT('%', #{shValue},'%')
	AND a.id LIKE CONCAT('%', #{shValue},'%')
	AND a.email LIKE CONCAT('%', #{shValue},'%')



	AND a.dob BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.regDatetime BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO member(
	name
	,id
	,password
	,position
	,gender
	,dob
	,telCompany
	,phone
	,phone2
	,emailInsert
	,emailDomain
	,email
	,mobileNY
	,emailNY
	,personalInfo
	,delNY
	,regDatetime
	,company
	,creatorNickname
)
VALUES (
	#{name}
	,#{id}
	,#{password}
	,#{position}
	,#{gender}
	,#{dob}
	,#{telCompany}
	,#{phone}
	,#{phone2}
	,#{emailInsert}
	,#{emailDomain}
	,#{email}
	,#{mobileNY}
	,#{emailNY}
	,#{personalInfo}
	,0
	,now()
	,#{company}
	,#{creatorNickname}
)


	SELECT last_insert_id()

INSERT INTO memberUploaded (
    type
    ,defaultNY
    ,sort
    ,originalName
    ,uuidName
    ,ext
    ,size
    ,uploadDelNY
    ,pseq
    ,regDate
    ,path
   )
   VALUES (
	#{type}
	,#{defaultNY}
	,#{sort}
	,#{originalName}
	,#{uuidName}
	,#{ext}
	,#{size}
	,#{uploadDelNY}
	,#{pseq}
	,now()
	,#{path}
	)

	SELECT last_insert_id()

INSERT INTO transport (
	zipcode
	,address
	,addressDetail
	,addr3
	,member_seq
)
VALUES (
	#{zipcode}
	,#{address}
	,#{addressDetail}
	,#{addr3}
	,#{seq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	position = #{position}
	,seq = #{seq}
	,name = #{name}
	,id = #{id}
	,password = #{password}
	,company = #{company}
	,creatorNickname = #{creatorNickname} 
	,dob = #{dob}
	,gender = #{gender}
	,telCompany = #{telCompany}
	,phone = #{phone}
	,phone2 = #{phone2}
	,email = CONCAT(#{emailInsert},

		'@naver.com')
		'@gmail.com')
		'@daum.net')

	,emailInsert = #{emailInsert}
	,emailDomain = #{emailDomain}
	,mobileNY = #{mobileNY}
	,emailNY = #{emailNY}
	,personalInfo = #{personalInfo}
	WHERE
	seq = #{seq}

UPDATE member
