mapper.select:
select a.seq ,a.name ,a.id ,a.position ,a.gender ,a.dob ,a.phone ,a.email ,a.mobileNY ,a.emailNY ,a.personalInfo ,a.delNY ,a.regDatetime FROM member a WHERE 1=1
AND a.delNY = #{shdelNY} AND a.gender = #{shGender} AND a.position = #{shPosition}
AND a.name LIKE CONCAT('%', #{shValue},'%') AND a.id LIKE CONCAT('%', #{shValue},'%') AND a.email LIKE CONCAT('%', #{shValue},'%')
AND a.dob BETWEEN #{shDateStart} AND #{shDateEnd} AND a.regDatetime BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO member(
	name
	,id
	,password
	,position
	,gender
	,dob
	,telCompany
	,phone
	,phone2
	,emailInsert
	,emailDomain
	,mobileNY
	,emailNY
	,personalInfo
	,delNY
	,regDatetime
	,company
	,creatorNickname
)
VALUES (
	#{name}
	,#{id}
	,#{password}
	,#{position}
	,#{gender}
	,#{dob}
	,#{telCompany}
	,#{phone}
	,#{phone2}
	,#{emailInsert}
	,#{emailDomain}
	,#{mobileNY}
	,#{emailNY}
	,#{personalInfo}
	,0
	,now()
	,#{company}
	,#{creatorNickname}
)

	SELECT last_insert_id()
