mapper.sql:
from classOrder a left join member b on b.seq = a.member_seq left join transport c on c.seq = a.transport_seq left join classProduct d on d.seq = a.classProduct_seq WHERE 1=1
AND a.delNY = #{shDelNY}
AND b.name LIKE CONCAT('%', #{shValue},'%') AND b.id LIKE CONCAT('%', #{shValue},'%') AND b.email LIKE CONCAT('%', #{shValue},'%') AND c.address LIKE CONCAT('%', #{shValue},'%') AND a.price = #{shValue}
AND a.orderDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO classOrder a(
	b.name
	,b.phone
	,c.zipcode
	,c.address
	,c.addressDetail
	,a.request
	,a.price
	,a.priceDiscount
	,a.couponDiscount
	,a.finalPrice
	,a.pay
	,a.orderDate
)
VALUES (
	#{b.name}
	,#{b.phone}
	,#{c.zipcode}
	,#{c.address}
	,#{c.addressDetail}
	,#{a.request}
	,#{a.price}
	,#{a.priceDiscount}
	,#{a.couponDiscount}
	,#{a.finalPrice}
	,#{a.pay}
	,now()
)
from classOrder a 
left join member b on b.seq = a.member_seq
left join transport c on c.aSeq = a.transport_seq
left join classProduct d on d.seq = a.classProduct_seq
 WHERE 1=1

	SELECT last_insert_id()

mapper.update:
UPDATE classOrder
SET
	b.name = #{b.name}
	,b.phone = #{b.phone}
	,c.zipcode = #{c.zipcode}
	,c.address = #{c.address}
	,c.addressDetail = #{c.addressDetail}
	,a.request = #{a.request}
	,a.price = #{a.price}
	,a.priceDiscount = #{a.priceDiscount}
	,a.couponDiscount = #{a.couponDiscount}
	,a.finalPrice = #{a.finalPrice}
	,a.pay = #{a.pay}
	,a.orderDate = now()
WHERE
	seq = #{seq}
from classOrder a 
left join member b on b.seq = a.member_seq
left join transport c on c.seq = a.transport_seq
left join classProduct d on d.seq = a.classProduct_seq
 WHERE 1=1
