mapper.sql:
from classOrder a left join member b on b.seq = a.member_seq left join transport c on a.member_seq = c.member_seq AND c.tOrder = 1 left join classProduct d on d.seq = a.classProduct_seq ORDER BY a.seq DESC
AND a.delNY = #{shDelNY}
AND b.name LIKE CONCAT('%', #{shValue},'%') AND b.id LIKE CONCAT('%', #{shValue},'%') AND b.email LIKE CONCAT('%', #{shValue},'%') AND c.address LIKE CONCAT('%', #{shValue},'%') AND a.price = #{shValue}
AND a.orderDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO classOrder a(
	b.name
	,b.phone
	,c.zipcode
	,c.address
	,c.addressDetail
	,c.addr3
	,a.request
	,a.price
	,a.priceDiscount
	,a.couponDiscount
	,a.finalPrice
	,a.pay
	,a.orderDate
)
VALUES (
	#{b.name}
	,#{b.phone}
	,#{c.zipcode}
	,#{c.address}
	,#{c.addressDetail}
	,#{c.addr3}
	,#{a.request}
	,#{a.price}
	,#{a.priceDiscount}
	,#{a.couponDiscount}
	,#{a.finalPrice}
	,#{a.pay}
	,now()
)


	SELECT last_insert_id()

INSERT INTO classOrder(
	request
	,price
	,priceDiscount
	,couponDiscount
	,finalPrice
	,pay
	,orderDate
	,delNY
	,tOrder
	,classProduct_seq
	,member_seq
)
VALUES (
	#{request}
	,#{price}
	,#{priceDiscount}
	,#{couponDiscount}
	,#{finalPrice}
	,#{pay}
	,now()
	,0
	,1
	,#{classProduct_seq}
	,#{member_seq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE classOrder
SET
	b.name = #{b.name}
	,b.phone = #{b.phone}
	,c.zipcode = #{c.zipcode}
	,c.address = #{c.address}
	,c.addressDetail = #{c.addressDetail}
	,c.addr3 = #{c.addr3}
	,a.request = #{a.request}
	,a.price = #{a.price}
	,a.priceDiscount = #{a.priceDiscount}
	,a.couponDiscount = #{a.couponDiscount}
	,a.finalPrice = #{a.finalPrice}
	,a.pay = #{a.pay}
	,a.orderDate = now()

	WHERE
		seq = #{seq}
