mapper.sql:
FROM classProduct a
WHERE 1=1

AND a.classDelNY = #{shDelNY}



	AND a.category LIKE CONCAT('%', #{shValue},'%')
	AND a.title LIKE CONCAT('%', #{shValue},'%')
	AND a.classDiv LIKE CONCAT('%', #{shValue},'%')
	AND a.price = #{shValue}



	AND a.classReg BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.classMod BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO classProduct(
	classDelNY
	,category
	,classDiv
	,title
	,price
	,discountRate
	,payMonth
	,classAmount
	,dateNY
	,subtitleNY
	,kitNY
	,classInfo
	,curriculum
	,classReg
)
VALUES (
	0		
	,#{category}
	,#{classDiv}
	,#{title}
	,#{price}
	,#{discountRate}
	,#{payMonth}
	,#{classAmount}
	,#{dateNY}
	,#{subtitleNY}
	,#{kitNY}
	,#{classInfo}
	,#{curriculum}
	,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE classProduct
SET
	classDelNY = #{classDelNY}
	,category = #{category}
	,classDiv = #{classDiv}
	,title = #{title}
	,price = #{price}
	,discountRate = #{discountRate}
	,payMonth = #{payMonth}
	,classAmount = #{classAmount}
	,dateNY = #{dateNY}
	,subtitleNY = #{subtitleNY}
	,kitNY = #{kitNY}
	,classInfo = #{classInfo}
	,curriculum = #{curriculum}
	,classMod = now()
WHERE
	seq = #{seq}
