mapper.sql:
FROM review a
left join classProduct b on b.seq = a.classProduct_seq
WHERE 1=1

AND a.reviewDelNY = #{shdelNY}



	AND a.id LIKE CONCAT('%', #{shValue},'%')
	AND a.content LIKE CONCAT('%', #{shValue},'%')



	AND a.reviewRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.reviewModDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO review(
      id
      ,preferenceStar
      ,content
      ,classProgressRate
      ,reviewLike
      ,reviewDelNY
      ,reviewRegDate
)
VALUES (
	  #{id}
      ,#{preferenceStar}
      ,#{content}
      ,#{classProgressRate}
      ,#{reviewLike}
      ,0
      ,now()
)


	SELECT last_insert_id()

INSERT INTO member (
	name
	,id
)
VALUES (
	#{name}
	,#{id}
)

	SELECT last_insert_id()

mapper.update:
UPDATE review
SET
	id = #{id}
	,preferenceStar = #{preferenceStar}
	,content = #{content}
	,classProgressRate = #{classProgressRate}
	,reviewLike = #{reviewLike}
	,reviewDelNY = #{reviewDelNY}
	,reviewModDate = now()
	WHERE
	seq = #{seq}
